// src/screens/GameScreen.tsx — versión completa y corregida
import React, { useEffect, useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Button,
  Alert,
  FlatList,
  TouchableOpacity,
  TextInput,
} from 'react-native';
import firestore from '@react-native-firebase/firestore';
import { useRoute, useNavigation, RouteProp } from '@react-navigation/native';
import type { NativeStackNavigationProp } from '@react-navigation/native-stack';
import { useAuth } from '../context/AuthContext';
import { checkAndAdvanceRound } from '../utils/roundManager';

/* ============ Tipos ============ */

type RootStackParamList = {
  Game: { gameId: string };
  EndGame: { gameId: string };
  [key: string]: object | undefined;
};

type GameRouteProp = RouteProp<RootStackParamList, 'Game'>;
export type NavigationProp = NativeStackNavigationProp<RootStackParamList>;

type AuctionType = 'open' | 'sealed' | 'once' | 'fixed' | 'double';

export interface Card {
  id: string;
  title: string;
  artist: string;
  auctionType: AuctionType;
}

interface Player {
  uid: string;
  name: string;
  money: number;
  hand: Card[];
}

interface CurrentAuction {
  artwork: string;
  artist: string;
  sellerId: string;
  type: AuctionType;
  highestBid: number;
  highestBidder: string | null;
  bids?: Record<string, number>; // sealed / once
  onceOrder?: string[];
  currentBidIndex?: number;
  fixedPrice?: number;
  passes?: string[];
}

/* ============ Componente ============ */

export default function GameScreen() {
  const { user } = useAuth();
  const route = useRoute<GameRouteProp>();
  const navigation = useNavigation<NavigationProp>();
  const { gameId } = route.params;

  const [game, setGame] = useState<any | null>(null);
  const [selectedCard, setSelectedCard] = useState<Card | null>(null);
  const [secondCard, setSecondCard] = useState<Card | null>(null);
  const [doubleMode, setDoubleMode] = useState(false);
  const [sealedBid, setSealedBid] = useState('');
  const [fixedConfirm, setFixedConfirm] = useState(false);

  useEffect(() => {
    const unsub = firestore()
      .collection('games')
      .doc(gameId)
      .onSnapshot((doc) => {
        const data = doc.data();
        if (!data) return;
        setGame(data);
        if (data.status === 'finished') navigation.navigate('EndGame', { gameId });
      });
    return () => unsub();
  }, [gameId]);

  useEffect(() => {
    if (!game) return;
    const auction: CurrentAuction | undefined = game.currentAuction;
    if (!auction || auction.type !== 'sealed') return;
    const bids = auction.bids ?? {};
    if (Object.keys(bids).length === game.players.length) {
      const [winnerId, amount] = (Object.entries(bids) as [string, number][]).sort((a, b) => b[1] - a[1])[0];
      finalizeAuction(winnerId, amount);
    }
  }, [game?.currentAuction?.bids]);

  const finalizeAuction = (winnerId: string, amount: number) => {
    const updatedPlayers = game.players.map((p: Player) => {
      if (p.uid === winnerId) {
        return { ...p, money: p.money - amount };
      }
      if (p.uid === game.currentAuction?.sellerId) {
        return { ...p, money: p.money + amount };
      }
      return p;
    });

    updateGame({
      players: updatedPlayers,
      currentAuction: null,
    });
  };

  if (!game || !user) {
    return (
      <View style={[styles.container, styles.center]}>
        <Text>Cargando…</Text>
      </View>
    );
  }

  const me: Player = game.players.find((p: Player) => p.uid === user.uid)!;
  const isMyTurn = game.currentTurn === user.uid;
  const activePlayer: Player = game.players.find((p: Player) => p.uid === game.currentTurn)!;
  const auction: CurrentAuction | null = game.currentAuction ?? null;

  const updateGame = (data: Partial<any>) => firestore().collection('games').doc(gameId).update(data);
  const updateAuction = (data: Partial<CurrentAuction>) => updateGame({ currentAuction: { ...(auction ?? {}), ...data } });

  const nextTurn = () => {
    const idx = game.players.findIndex((p: Player) => p.uid === game.currentTurn);
    const next = game.players[(idx + 1) % game.players.length].uid;
    return updateGame({ currentTurn: next });
  };

  const removeCards = (cards: Card[]) => {
    const remaining = me.hand.filter((c) => !cards.some((s) => s.id === c.id));
    const updatedPlayers = game.players.map((p: Player) => (p.uid === me.uid ? { ...p, hand: remaining } : p));
    updateGame({ players: updatedPlayers });
  };

  const resetSelections = () => {
    setSelectedCard(null);
    setSecondCard(null);
    setDoubleMode(false);
    setSealedBid('');
    setFixedConfirm(false);
  };

  const launchAuction = async (cards: Card[], type: AuctionType) => {
    const artwork = cards.map((c) => c.title).join(' + ');
    const artist = cards[0].artist;

    const base: CurrentAuction = {
      artwork,
      artist,
      sellerId: me.uid,
      type,
      highestBid: 0,
      highestBidder: null,
      bids: {},
      passes: [],
    };

    if (type === 'fixed') base.fixedPrice = 100;

    if (type === 'once') {
      const sellerIdx = game.players.findIndex((p: Player) => p.uid === me.uid);
      const order = [...game.players.slice(sellerIdx + 1), ...game.players.slice(0, sellerIdx)].map((p) => p.uid);
      base.onceOrder = order;
      base.currentBidIndex = 0;
    }

    await updateGame({ currentAuction: base });
    removeCards(cards);
    resetSelections();
  };

  const handleStartAuction = () => {
    if (!selectedCard) return Alert.alert('Elegí una carta');
    if (selectedCard.auctionType === 'double') return setDoubleMode(true);
    launchAuction([selectedCard], selectedCard.auctionType);
  };

  const confirmDoubleAuction = () => {
    if (!secondCard) return Alert.alert('Elegí la segunda carta');
    if (secondCard.artist !== selectedCard!.artist) return Alert.alert('Deben ser del mismo artista');
    launchAuction([selectedCard!, secondCard], secondCard.auctionType);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Tu mano:</Text>
      <FlatList
        data={me.hand}
        keyExtractor={(item) => item.id}
        horizontal
        renderItem={({ item }) => (
          <TouchableOpacity
            style={[styles.card, selectedCard?.id === item.id && styles.selectedCard]}
            onPress={() => setSelectedCard(item)}
          >
            <Text>{item.title}</Text>
            <Text>{item.artist}</Text>
            <Text>{item.auctionType}</Text>
          </TouchableOpacity>
        )}
      />
      {selectedCard && (
        <Button title="Iniciar Subasta" onPress={handleStartAuction} />
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: '#fff',
  },
  center: {
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  card: {
    padding: 12,
    margin: 8,
    borderWidth: 1,
    borderRadius: 8,
    borderColor: '#ccc',
    backgroundColor: '#f9f9f9',
  },
  selectedCard: {
    borderColor: 'blue',
    backgroundColor: '#e0f7ff',
  },
});

