const generateRoomCode = (): string => {
  const letters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
  const numbers = '0123456789';
  const code =
    letters[Math.floor(Math.random() * 26)] +
    letters[Math.floor(Math.random() * 26)] +
    letters[Math.floor(Math.random() * 26)] +
    letters[Math.floor(Math.random() * 26)] +
    numbers[Math.floor(Math.random() * 10)] +
    numbers[Math.floor(Math.random() * 10)];
  return code;
};

/**
 * Crea una nueva sala de juego en la colección 'games'.
 * Devuelve el ID interno y el código público de la sala.
 */
export const createRoom = async (
  hostId: string,
  displayName?: string,
  photoURL?: string
): Promise<{ gameId: string; roomCode: string }> => {
  const roomCode = generateRoomCode();

  const hostPlayer = {
    uid: hostId,
    name: typeof displayName === 'string' ? displayName : 'Anónimo',
    photoURL: typeof photoURL === 'string' ? photoURL : undefined,
    money: 100,
    hand: [],
    isHost: true,
  };

  const newGame = {
    hostId,
    createdAt: Date.now(),
    roomCode,
    round: 1,
    status: 'waiting',
    currentTurn: null,
    players: [hostPlayer],
  };

  const gameRef = await firestore().collection('games').add(newGame);
  return { gameId: gameRef.id, roomCode };
};

/**
 * Permite a un jugador unirse a una sala usando el código.
 */
export const joinRoom = async (
  code: string,
  uid: string,
  displayName?: string,
  photoURL?: string
): Promise<string> => {
  const query = await firestore()
    .collection('games')
    .where('roomCode', '==', code)
    .limit(1)
    .get();

  if (query.empty) {
    throw new Error('Sala no encontrada');
  }

  const gameDoc = query.docs[0];
  const gameId = gameDoc.id;
  const gameData = gameDoc.data();

  const player = {
    uid,
    name: typeof displayName === 'string' ? displayName : 'Anónimo',
    photoURL: typeof photoURL === 'string' ? photoURL : undefined,
    money: 100,
    hand: [],
    isHost: false,
  };

  // Previene duplicación de jugadores
  const filtered = gameData.players?.filter((p: any) => p.uid !== uid) || [];
  filtered.push(player);

  await firestore().collection('games').doc(gameId).update({
    players: filtered,
  });

  return gameId;
};

