// src/utils/gameSetup.ts
import firestore from '@react-native-firebase/firestore';

export type Card = {
  id: string;
  title: string;
  artist: string;
  auctionType: 'open' | 'sealed' | 'once' | 'double';
};

const artists = ['Van Gogh', 'Picasso', 'Kahlo', 'Dalí', 'Matisse'];
const allAuctionTypes = ['open', 'sealed', 'once', 'double'] as const;

function generateDeck(): Card[] {
  const deck: Card[] = [];
  let idCounter = 1;

  for (let i = 0; i < 50; i++) {
    const artist = artists[i % artists.length];
    const auctionType = allAuctionTypes[i % allAuctionTypes.length];
    deck.push({
      id: `card-${idCounter++}`,
      title: `Obra ${i + 1}`,
      artist,
      auctionType,
    });
  }

  return deck;
}

function shuffle<T>(array: T[]): T[] {
  const arr = [...array];
  for (let i = arr.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [arr[i], arr[j]] = [arr[j], arr[i]];
  }
  return arr;
}

export async function dealInitialHands(gameId: string) {
  const gameRef = firestore().collection('games').doc(gameId);
  const doc = await gameRef.get();
  const gameData = doc.data(); 
  if (!gameData) {
    console.log('❌ gameData vacío');
    return;
  }

  const deck = generateDeck();
  const shuffled = shuffle(deck);
  const players = gameData.players;
  const cardsPerPlayer = 10;

  if (players.length * cardsPerPlayer > shuffled.length) {
    console.warn('⚠️ No hay suficientes cartas para repartir a todos los jugadores');
    return;
  }

  const playersWithHands = players.map((p: any, i: number) => {
    const start = i * cardsPerPlayer;
    const end = start + cardsPerPlayer;
    const hand = shuffled.slice(start, end);
    console.log(`🎴 Asignando ${hand.length} cartas a ${p.name}`);
    return { ...p, hand };
  });

  console.log('✅ Actualizando Firestore con manos repartidas...');
  await gameRef.update({ players: playersWithHands });
}

